CREATE TABLE `achat` (
  `id` tinyint(10) NOT NULL AUTO_INCREMENT,
  `nom` varchar(255) NOT NULL,
  `tome` varchar(255) NOT NULL,
  `date_sortie` varchar(255) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=latin1;

-- -----------------------------
-- creation de la table anime
-- -----------------------------
CREATE TABLE `anime` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nom` varchar(200) DEFAULT NULL,
  `episode` varchar(200) DEFAULT NULL,
  `date` varchar(200) DEFAULT NULL,
  `theme` varchar(500) DEFAULT NULL,
  `saison` varchar(250) DEFAULT NULL,
  `duree` varchar(200) DEFAULT NULL,
  `format` varchar(255) DEFAULT NULL,
  `oav` varchar(255) DEFAULT NULL,
  `film` varchar(255) DEFAULT NULL,
  `image` varchar(255) DEFAULT NULL,
  `date_modif` datetime(1) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=205 DEFAULT CHARSET=utf8;

-- -----------------------------
-- creation de la table manga
