CREATE TABLE `manga` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nom` varchar(50) CHARACTER SET latin1 DEFAULT NULL,
  `tome` varchar(25) CHARACTER SET latin1 DEFAULT NULL,
  `chapitre` varchar(25) CHARACTER SET latin1 DEFAULT NULL,
  `datecreation` varchar(25) CHARACTER SET latin1 DEFAULT NULL,
  `genre` varchar(25) CHARACTER SET latin1 DEFAULT NULL,
  `statut` varchar(25) CHARACTER SET latin1 DEFAULT NULL,
  `theme` varchar(100) CHARACTER SET latin1 DEFAULT NULL,
  `Resume` varchar(5000) CHARACTER SET latin1 DEFAULT NULL,
  `format` varchar(255) CHARACTER SET latin1 DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=110 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;



